window.renderCourses=function(e,t){const r=document.createDocumentFragment(),o=document.querySelector(".courses-list__template").content.querySelector(".courses-list__item");for(let n=0;n<t;n++){let t=o.cloneNode(!0);t.querySelector(".courses-list__name").textContent=e[n].name,t.querySelector(".courses-list__date").textContent=e[n].date,t.querySelector("a").href=e[n].source,r.appendChild(t)}return r};